int main() {
    int num,i,f,r,sum,temp;
    int max;
    printf("Enter maximum range: ");
    scanf("%d",&max);
    printf("Strong numbers in given range are: ");
    for (num=1; num <= max; num++) {
        temp = num;
        sum=0;
        while(temp) {
            i=1;
            f=1;
            r=temp%10;
            while(i<=r) {
                f=f*i;
                i++;
            }
            sum=sum+f;
            temp=temp/10;
        }
        if(sum==num)
