using namespace std;
int main()
{
    int arr[10], brr[10], c= 0, i, j;
    cout<<"Enter 10 elements of an array-\n";
    for ( i= 0; i <10; i++)
        cin>>arr[i];
    for (i = 0; i <10; i++)
    {
        for (j = 0; j < c; j++)
        {
            if(arr[i] == brr[j])
                break;
        }
        if (j == c)
        {
            brr[c] = arr[i];
            c++;
        }
    }
    cout<<"Array obtained after removing duplicate elements:\n";
    for (i = 0; i < c; i++)
        cout<<brr[i]<<"  ";
}

