//Write a C program to enter two numbers and perform all arithmetic operations.
#include<stdio.h>
void main()
{
    int a,b,c,d,x,y;
    scanf("%d%d",&x,&y);
    a=x+y;
    b=x-y;
    c=x*y;
    d=x/y;
    printf("add=%d\n",a);
    printf("sub=%d\n",b);
    printf("multi=%d\n",c);
    printf("divi=%d\n",d);
}

//Write a C++ program to print Hello World.
#include<iostream>
using namespace std;
int main()
{
    int a,b,c,d,x,y;
    cin >> x >> y;
    a=x+y;
    b=x-y;
    c=x*y;
    d=x/y;

    cout << add= a << endl;
    cout << sub= b << endl;
    cout << multi=c << endl;
    cout << divi=d << endl;
    return 0;
}

//Write a Java program to print Hello World.
public class Funycode {
    public static void main(String[] args) {
        int a,b,c,d,x,y;
        Scanner sc=new Scanner(System.in);
        x = sc.nextInt();
        y = sc.nextInt();
        a=x+y;
        b=x-y;
        c=x*y;
        d=x/y;
        System.out.println("add=" + a);
        System.out.println("sub=" + b);
        System.out.println("multi=" + c);
        System.out.println("divi=" +d);
    }
}

