//Write a C program to copy all elements from an array to another array.
#include <stdio.h>
void main()
{
    int arr1[10], arr2[10],i;
    printf("Input 10 elements in the array :\n");
       for(i=0;i<10;i++)
        {
	      scanf("%d",&arr1[i]);
	    }
    for(i=0; i<10; i++)
    {
        arr2[i] = arr1[i];
    }
    printf("\nThe elements stored in the first array are :\n");
    for(i=0; i<10; i++)
    {
        printf("% d", arr1[i]);
    }
    printf("\n\nThe elements copied into the second array are :\n");
    for(i=0; i<10; i++)
    {
        printf("% d", arr2[i]);
    }
}

//Write a C++ program to copy all elements from an array to another array.

#include<iostream>
using namespace std;
int main()
{
    int arr1[10], arr2[10],i;
    cout<<"Input 10 elements in the array :\n";
    for(i=0; i<10; i++)
    {
        cin>>arr1[i];
    }
    for(i=0; i<10; i++)
    {
        arr2[i] = arr1[i];
    }
    cout<<"\nThe elements stored in the first array are :\n";
    for(i=0; i<10; i++)
    {
        cout<<arr1[i]<<" ";
    }
    cout<<"\nThe elements copied into the second array are :\n";
    for(i=0; i<10; i++)
    {
        cout<<arr2[i]<<"  ";
    }
}

