    public static void main(String[] args) {

        Scanner z=new Scanner(System.in);
        int arr1[]=new int[10], arr2[]=new int[10],i;
        System.out.println("Input 10 elements in the array :\n");
        for(i=0; i<10; i++)
        {
            arr1[i]=z.nextInt();
        }
        for(i=0; i<10; i++)
        {
            arr2[i] = arr1[i];
        }
        System.out.println("\nThe elements stored in the first array are :\n");
        for(i=0; i<10; i++)
        {
        	System.out.println(arr1[i]+" ");
        }
        System.out.println("\nThe elements copied into the second array are :\n");
        for(i=0; i<10; i++)
        {
        	System.out.println(arr2[i]+"  ");
        }

    }

}

