import java.util.Scanner;
public class Funycode {
    public static void main(String[] args) {

        Scanner z=new Scanner(System.in);
        int n,a,b,c,d,e,f,g,h,i;
        System.out.print("Enter given amount-:\n");
        n=z.nextInt();
        a=n/1000;
