        if(c>0)
            System.out.println("tk100="+c+"\n");
        d=(((n%1000)%500)%100)/50;
        if(d>0)
            System.out.println("tk50="+d+"\n");
        e=((((n%1000)%500)%100)%50)/20;
        if(e>0)
            System.out.println("tk20="+e+"\n");
        f=(((((n%1000)%500)%100)%50)%20)/10;
        if(f>0)
            System.out.println("tk10="+f+"\n");
        g=((((((n%1000)%500)%100)%50)%20)%10)/5;
        if(g>0)
            System.out.println("tk5="+g+"\n");
        h=(((((((n%1000)%500)%100)%50)%20)%10)%5)/2;
        if(h>0)
            System.out.println("tk2="+h+"\n");
        i=((((((((n%1000)%500)%100)%50)%20)%10)%5)%2);
        if(i==1)
            System.out.println("tk1="+i+"\n");
    }
}

