//Write a C program to check whether a string is palindrome or not.
#include<stdio.h>
#include<string.h>
int main()
{
    char str1[50],str2[50];
    int cmp;
    cout << "Enter  the string : ";
    gets(str1);
    strcpy(str2,str1);
    strrev(str1);
    cmp = strcmp(str1,str2);
    if(cmp==0)
        cout << "palindrome";
    else
        cout << "Not palindrome";
}

