//Write a C program to read 10 numbers from keyboard and find their sum and average
//Using for loop
#include<stdio.h>
void main()
{
    int i,a,sum=0,avg;
    for(i=1;i<=10;i++)
    {
        scanf("%d",&a);
        sum=sum+a;
    }
    avg=sum/10;
    printf("sum = %d\n",sum);
    printf("avg = %d",avg);
}

//Using while loop
#include<stdio.h>
void main()
{
    int i=1,a,sum=0,avg;
    while(i<=10)
    {
        scanf("%d",&a);
        sum=sum+a;
        i++;
    }
   avg=sum/10;
    printf("sum = %d\n",sum);
    printf("avg = %d",avg);
}
//Using do while loop
#include<stdio.h>
void main()
{
    int i=1,a,sum=0,avg;
    do
    {
        scanf("%d",&a);
        sum=sum+a;
        i++;
    }
    while(i<=10);
    avg=sum/10;
    printf("sum = %d\n",sum);
    printf("avg = %d",avg);
}

