            if(*p=='a'|| *p=='e'||*p=='i'||*p=='o'||*p=='u'||*p=='A'||*p=='E'||*p=='I'||*p=='O' ||*p=='U')
                vow++;
            else
                con++;
        }
        p++;
    }
    cout<<"vowels ="<<vow<<endl;
     cout<<"consonants = "<<con;
}

