//Write a C program to check whether a number is Armstrong number or not.
//Using for loop
#include<stdio.h>
void main()
{
    int i,n,r,sum=0;
    printf("Input a number: ");
    scanf("%d",&n);
    for(i=n;n!=0;n=n/10){
         r=n % 10;
         sum=sum+(r*r*r);
    }
    if(sum==i)
         printf("%d is an Armstrong number.\n",i);
    else
         printf("%d is not an Armstrong number.\n",i);
}
//Using while loop
#include<stdio.h>
void main()
{
    int i,n,r,sum=0;
    printf("Input  a number: ");
    scanf("%d",&n);
    i=n;
    while(n!=0)
    {
        r=n % 10;
        sum=sum+(r*r*r);
        n=n/10;
    }
    if(sum==i)
        printf("%d is an Armstrong number.\n",i);
    else
        printf("%d is not an Armstrong number.\n",i);
}
//Using do while loop
#include<stdio.h>
void main()
{
    int i,n,r,sum=0;
    printf("Input  a number: ");
    scanf("%d",&n);
    i=n;
    do
    {
        r=n % 10;
        sum=sum+(r*r*r);
        n=n/10;
    }
    while(n!=0);
    if(sum==i)
        printf("%d is an Armstrong number.\n",i);
    else
        printf("%d is not an Armstrong number.\n",i);
}

