/*Write a C program to find the maximum between two numbers using switch-case.*/
#include<stdio.h>
void main()
{
   int a,b;
   printf("Enter Number:");
   scanf("%d %d",&a,&b);
   switch(a > b)
   {
      case 0:
         printf("maximum number %d",b);
         break;
      case 1:
         printf("maximum number %d",a);
         break;
      default:
         printf("Both are equal");
   }
}

/*Write a C++ program to find the maximum between two numbers using switch-case.*/
#include<iostream>
using namespace std;
int main()
{
   int a,b;
   cout << "Enter Number:";
   cin >> a >> b;
   switch(a > b)
   {
      case 0:
         cout <<"maximum number " << b;
         break;
      case 1:
         cout << "maximum number " << a;
         break;
      default:
         cout << "Both are equal";
   }
}

/*Write a Java program to find the maximum between two numbers using switch-case.*/
import java.util.Scanner;
public class Funycode {
    public static void main(String[] args) {

        Scanner z=new Scanner(System.in);
        int a,b;
        System.out.println("Input Two Integer");
        a=z.nextInt();
        b=z.nextInt();
        switch(a > b)
        {
            case 0:
                System.out.println("maximum number " + b);
                break;
            case 1:
                System.out.println("maximum number " + a);
                break;
            default:
                System.out.println("Both are equal");
        }

    }
}

