//Write a C program to Calculate the Value of cos(x)
#include<stdio.h>

float Cosx(float x,int n)
{
    int fact,sign=-1,i,j;
    float p,sum=0.0;
    for (i = 2; i <= n; i += 2)
    {
        p = 1;
        fact = 1;
        for (j = 1; j <= i; j++)
        {
            p = p * x;
            fact = fact * j;
        }

        sum += sign * p / fact;
        sign =  - 1 * sign;
    }
    return sum;
}

int main()
{
    float x;
    int n;
    printf("Enter value of x: ");
    scanf("%f",&x);
    printf("Enter value of n: ");
    scanf("%d",&n);

    float val=Cosx(x,n);

    printf("cos(%0.2f) = %.2f", x, 1+val);
    return 0;
}

