#include<stdio.h>
int main()
{
    int cy,yj;
    printf("Enter the current year and the year of joining-");
    scanf("%d %d",&cy,&yj);
    if(cy-yj>3)
        printf("bonus of Rs 2500/-");
    else
        printf("program error");
}

