//Write a C program to print all strong numbers between given interval using functions.
#include<stdio.h>

int fact(int n)
{
    int total=1;
    for(int i=1;i<=n;i++)
        total*=i;
    return total;
}

void strongNumber(int a,int b)
{
    int tmp,num,r;
    for(int i=a;i<=b;i++)
    {
        int sum=0;
        num=i;
        while(num)
        {
            r=num%10;
            sum=sum+fact(r);
            num=num/10;
        }
        if(sum==i)
            printf("\nThe number %d is a strong number",i);
        else
            printf("\nThe number %d is not a strong number",i);
    }
}

int main()
{
    int a,b;
    printf("Enter a range: ");
    scanf("%d%d",&a,&b);

    strongNumber(a,b);

    return 0;
}

