/*Write a C program to take the value from the user as input any character and check whether it is the alphabet, digit or special character using the switch.*/

#include<stdio.h>
int main()
{
    char num;
    printf("Enter a character:");
    scanf("%c",&num);
    switch(num>='a'&& num<='z'||num>='A'&& num<='Z')
    {
        case 1:
            printf("alphabet");
            break;
        case 0:
            switch(num>='0' && num<='9')
            {
                case 1:
                    printf("It's digit");
                    break;
                case 0:
                    printf("special character");
                    break;
            }
            break;
    }
}

/*Write a C++ program to take the value from the user as input any character and check whether it is the alphabet, digit or special character using the switch.*/
#include<iostream>
using namespace std;
int main()
{
    char num;
    cout << "Enter a character:";
    cin >>num;
    switch(num>='a'&& num<='z'||num>='A'&& num<='Z')
    {
        case 1:
            cout << "alphabet";
            break;
        case 0:
            switch(num>='0' && num<='9')
            {
                case 1:
                     cout << "It's digit";
                     break;
                case 0:
                     cout << "special character";
                     break;
            }
            break;
    }
    return 0;
}

/*Write a Java program to take the value from the user as input any character and check whether it is the alphabet, digit or special character using the switch.*/
import java.util.Scanner;
public class Funycode {
    public static void main(String[] args) {

        char num;
        System.out.println("Enter a character:");
        Scanner z = new Scanner();
        char num = z.next().charAt(0);
        switch(num>='a'&& num<='z'||num>='A'&& num<='Z')
        {
             case 1:
                 System.out.println("alphabet");
                 break;
             case 0:
                 switch(num>='0' && num<='9')
                 {
                      case 1:
                           System.out.println("It's digit");
                           break;
                      case 0:
                           System.out.println("special character");
                           break;
                 }
                 break;
        }
    }
}

