//Write a Java program to delete all vowels from a sentence. Assume that the sentence is not more than 80 characters long.
import java.util.Scanner;
public class Funycode {
    public static void main(String[] args) {
        Scanner z=new Scanner(System.in);
        System.out.println("Enter  the string : ");
        String str = z.nextLine();
        String str_new = "";
        char[] letter = {'a','e','i','o','u','A','E','I','O','U'};
        for (int i = 0; i < str.length(); i++ ){
            for (int j = 0; j < letter.length ; j ++){
                if (str.charAt(i) == letter[j]){
                    str.replace(str.charAt(i), ' ');
                }
            }
        }
        System.out.println(str);
    }
}

