#include <string.h>

int main()
{
    char s[1000],c,temp=1;
    int  i,j,k,count=0,n;

    printf("Enter  the string : ");
    gets(s);

    printf("Enter character: ");
    c=getchar();

    for(i=0;s[i];i++);
     n=i;


     while(i)
    {
       if(temp)
        {
            i--;
               if(c==s[i])
                {
                temp=0;
                s[i]=s[i+1];
                    i++;
             }


        }
        else
         {
             if(i==n)
            break;

           s[i]=s[i+1];
           i++;

         }

    }

      printf("%s",s);


    return 0;
}

