//Write a C program to count total number of duplicate elements in an array.

#include <stdio.h>
void main()
{
    int arr1[10],arr2[10],arr3[10],i,j,count=0,m=1;
    printf("Input 10 elements in the array :\n");
       for(i=0;i<10;i++)
        {
          scanf("%d",&arr1[i]);
        }
        for(i=0;i<10; i++)
        {
        arr2[i]=arr1[i];
        arr3[i]=0;
        }
    for(i=0;i<10; i++)
        {
        for(j=0;j<10;j++)
            {
                if(arr1[i]==arr2[j])
                {
                arr3[j]=m;
                m++;
                }
            }
            m=1;
        }
   for(i=0; i<10; i++)
    {
      if(arr3[i]==2)
        {
            count++;
      }
    }
      printf("The total number of duplicate elements found in the array is: %d \n",count);
}

//Write a C++ program to count total number of duplicate elements in an array.

#include<iostream>
using namespace std;
int main()
{
    int arr1[10],arr2[10],arr3[10],i,j,c=0,m=1;
    cout<<"Input 10 elements in the array :\n";
    for(i=0; i<10; i++)
    {
        cin>>arr1[i];
    }
    for(i=0; i<10; i++)
    {
        arr2[i]=arr1[i];
        arr3[i]=0;
    }
    for(i=0; i<10; i++)
    {
        for(j=0; j<10; j++)
        {
            if(arr1[i]==arr2[j])
            {
                arr3[j]=m;
                m++;
            }
        }
        m=1;
    }
    for(i=0; i<10; i++)
    {
        if(arr3[i]==2)
        {
            c++;
        }
    }
    cout<<"The total number of duplicate elements found in the array is: "<<c;
}

