//Write a C program to check whether a number is even or odd using conditional operator.
#include <stdio.h>
int main()
{
    int n;
    printf("Enter a number: ");
    scanf("%d", &n);
    (n%2 == 0)? printf("number is even"): printf("number is odd");
}

//Write a C++ program to check whether a number is even or odd using conditional operator.
#include<iostream>
using namespace std;
int main()
{
    int n;
    cout << "Enter a number: ";
    cin >> n;
    (n%2 == 0)? cout << "number is even": cout << "number is odd";
}

//Write a Java program to check whether a number is even or odd using conditional operator.
import java.util.Scanner;.
public class Funycode {
    public static void main(String[] args) {
      Scanner z = new Scanner(System.in);
      int n;
      System.out.println("Enter a number: ");
      n = z.nextInt();
      (n%2 == 0)? System.out.println("number is even"): System.out.println("number is odd");
    }
}

