//Write a C program to store elements in an array and print it.
#include <stdio.h>
void main()
{
    int arr[5],i,mx,mn;
    printf("Input 5 elements in the array :\n");
       for(i=0;i<5;i++)
        {
          scanf("%d",&arr[i]);
        }
    mx = arr[0];
    mn = arr[0];
    for(i=1; i<5; i++)
    {
        if(arr[i]>mx)
        {
            mx = arr[i];
        }
        if(arr[i]<mn)
        {
            mn = arr[i];
        }
    }
    printf("Maximum element is : %d\n", mx);
    printf("Minimum element is : %d\n\n", mn);
}

//Write a C++ program to store elements in an array and print it.

#include<iostream>
using namespace std;
int main()
{
    int arr[5],i,mx,mn;
    cout<<"Input 5 elements in the array :\n";
    for(i=0; i<5; i++)
    {
        cin>>arr[i];
    }
    mx = arr[0];
    mn = arr[0];
    for(i=1; i<5; i++)
    {
        if(arr[i]>mx)
        {
            mx = arr[i];
        }
        if(arr[i]<mn)
        {
            mn = arr[i];
        }
    }
    cout<<"\nMaximum element is : "<< mx;
    cout<<"\nMinimum element is : "<< mn;
}

//Write a Java program to store elements in an array and print it.
import java.util.Scanner;
public class Funycode {
    public static void main(String[] args) {

        Scanner z=new Scanner(System.in);
        int arr[]=new int[5],i,mx,mn;
        System.out.println("Input 5 elements in the array :\n");
        for(i=0; i<5; i++)
        {
            arr[i]=z.nextInt();
        }
        mx = arr[0];
        mn = arr[0];
        for(i=1; i<5; i++)
        {
            if(arr[i]>mx)
            {
                mx = arr[i];
            }
            if(arr[i]<mn)
            {
                mn = arr[i];
            }
        }
        System.out.println("\nMaximum element is : "+ mx);
        System.out.println("\nMinimum element is : "+ mn);
    }
}

