//Write a C program to sum of upper triangular matrix.
#include <stdio.h>
int main()
{
    int array1[3][3],i,j,sum=0;
    cout << "Enter 9 co-efficients of the matrix-\n";
    for (i = 0; i < 3; ++i)
    {
        for (j = 0; j < 3; ++j)
        {
            cin >> array[i][j];
        }
    }
    cout << "The given matrix is \n";
    for (i = 0; i < 3; ++i)
    {
        for (j = 0; j < 3; ++j)
            cout << "\t" << arr1[i][j];
        cout << "\n";
    }
    for (i = 0; i < 3; i++)
        for (j = 0; j < 3; j++)
        {
            if (i < j)
            {
                sum = sum + array1[i][j];
            }
        }
    cout << "\nSum of Upper Triangle Elements : " << sum;
}

