//Write a C program to check whether a number is positive, negative or zero using switch
#include <stdio.h>
int main()
{
    int num;
    printf("Enter any number: ");
    scanf("%d", &num);
    switch (num > 0)
    {
    case 1:
        printf("%d is positive", num);
        break;
    case 0:
        switch (num < 0)
        {
        case 1:
            printf("%d is negative", num);
            break;
        case 0:
            printf("%d is zero", num);
            break;
        }
        break;
    }
    return 0;
}

//Write a C++ program to check whether a number is positive, negative or zero using switch
#include<iostream>
using namespace std;
int main()
{
    int num;
    cout << "Enter any number: ";
    cin >> num;
    switch (num > 0)
    {
    case 1:
        cout << num << " is positive";
        break;
    case 0:
        switch (num < 0)
        {
        case 1:
            cout << num <<" is negative";
            break;
        case 0:
            cout << num << " is zero";
            break;
        }
        break;
    }
    return 0;
}

