/*Write a C program that calculates the volume of a sphere.*/
#include<stdio.h>
void main()
{
    int a;
    double b,c;
    scanf("%d",&a);
    c=4/3;
    b=c*3.1416*a*a*a;
    printf("volume=%f",b);
}

/*Write a C program that calculates the volume of a sphere.*/
#include<iostream>
using namespace std;
int main()
{
    int a;
    double b,c;
    cin>>a;
    c=4/3;
    b=c*3.1416*a*a*a;
    cout<<"volume:"<<b;
    return 0;
}

/*Write a java program that calculates the volume of a sphere.*/
import java.util.Scanner;
public class Funycode {
    public static void main(String[] args) {

        int a;
        double b,c;
        Scanner z=new Scanner(System.in);
        a=z.nextInt();
        c=4/3;
        b=c*3.1416*a*a*a;
        System.out.println("Perimeter="+b);
    }
}

