//Write a C program in C to find the number and sum of all integer between 100 and 200 which are divisible by 9.
//Using for loop
#include <stdio.h>

void main()
{
   int i, sum=0;
   printf("The Numbers are: \n");
   for(i=101;i<200;i++)
   {
     if(i%9==0)
     {
       printf("% 5d",i);
       sum+=i;
     }
   }
   printf("\n\nThe sum : %d \n",sum);
}
//Using while loop
#include <stdio.h>
void main()
{
   int i, sum=0;
   printf("The Numbers are: \n");
   i=101;
   while(i<200)
   {
     if(i%9==0)
     {
       printf("% 5d",i);
       sum+=i;
     }
     i++;
   }
   printf("\n\nThe sum : %d \n",sum);
}
//Using do while loop
#include <stdio.h>
void main()
{
   int i, sum=0;
   printf("The Numbers are: \n");
   i=101;
   do
   {
     if(i%9==0)
     {
       printf("% 5d",i);
       sum+=i;
     }
     i++;
   }while(i<200);
   printf("\n\nThe sum : %d \n",sum);
}

