//Write a C program to input a string and print it.
#include <stdio.h>
#include<string.h>
int main()
{
   char str1[20];
   char str2[20];
   int i;
   printf("Enter first string : ");
   scanf("%s",str1);
   printf("Enter second string : ");
   scanf("%s",str2);
   i=strcmp(str1,str2);
   if(i==0)
      printf("strings are equal");
   else
      printf("strings are not equal");
   return 0;
}

//Write a C++ program to input a string and print it.

#include <iostream>
using namespace std;
int  main()
{
   char str1[20];
   char str2[20];
   int i;
   cout << "Enter first string : ";
   cin >> str1;
   cout << "Enter second string : ";
   cin >> str2;
   i=strcmp(str1,str2);
   if(i==0)
      cout << "strings are equal";
   else
      cout << "strings are not equal";
   return 0;
}

