/*Write a C program to read the age of a candidate and determine whether it is eligible for casting his/her own vote.*/
#include<stdio.h>
int main()
{
    int a;
    printf("Enter a Age:");
    scanf("%d",&a);
    if(a>=18)
        printf("eligible for casting his/her vote");
    else
        printf("not eligible for casting his/her vote ");
}

/*Write a C++ program to read the age of a candidate and determine whether it is eligible for casting his/her own vote.*/
#include<iostream>
using namespace std;
int main()
{
    int a;
    cout<<"Enter a Age-\n";
    cin>>a;
    if(a>=18)
        cout<<"eligible for casting his/her vote";
    else
        cout<<"not eligible for casting his/her vote ";
}

