/*Write a C program to enter three angles and check whether the triangle valid or not.*/
#include<stdio.h>
void main()
{
    int a,b,c;
    printf("enter three angles of a triangle-");
    scanf("%d%d%d",&a,&b,&c);
    if((a+b+c==180)&&(a>0&&b>0&&c>0))
        printf(" triangle is valid");
    else
        printf("triangle is invalid");
}

/*Write a C++ program to enter three angles and check whether the triangle valid or not.*/
#include<iostream>
using namespace std;
int main()
{
    int a,b,c;
    cout<<"enter three angles of a triangle-\n";
    cin>>a>>b>>c;
    if((a+b+c==180)&&(a>0&&b>0&&c>0))
        cout<<" triangle is valid";
    else
        cout<<"triangle is invalid";
}

/*Write a java program to enter three angles and check whether the triangle valid or not.*/
import java.util.Scanner;
public class Funycode {
    public static void main(String[] args) {

        Scanner z=new Scanner(System.in);
        int a,b,c;
        System.out.print("enter three angles of a triangle-\n");
        a=z.nextInt();
        b=z.nextInt();
        c=z.nextInt();

        if((a+b+c==180)&&(a>0&&b>0&&c>0))
            System.out.print(" triangle is valid");
        else
            System.out.print("triangle is invalid");
    }
}

