//Write a C program to take the value from the user as input all sides of a triangle and check whether the triangle is valid or not using switch.
#include<stdio.h>
int main()
{
    int side1,side2,side3;
    printf("Enter three sides:");
    scanf("%d %d %d",&side1,&side2,&side3);
    switch((side1+side2>side3)||(side2+side3>side1)||(side3+side1>side2))
    {
    case 1:
        printf("valid");
        break;
    case 0:
        printf("invalid");
        break;
    default:
        printf("not triangle");
    }
}

//Write a C++ program to take the value from the user as input all sides of a triangle and check whether the triangle is valid or not using switch.
#include<iostream>
using namespace std;
int main()
{
    int side1,side2,side3;
    cout << "Enter three sides:";
    cin >> side1 >> side2 >> side3;
    switch((side1+side2>side3)||(side2+side3>side1)||(side3+side1>side2))
    {
    case 1:
        cout << "valid";
        break;
    case 0:
        cout << "invalid";
        break;
    default:
        cout << "not triangle";
    }
}

