//Write a C program to find maximum between two numbers using conditional operator.
#include <stdio.h>
int main()
{
    int a,b,max;
    printf("Enter two numbers: ");
    scanf("%d%d", &a, &b);
    if (a==b)
    {
        printf("Equal");
        return 0;
    }
    max = (a > b) ? a : b;
    printf("Maximum number is %d", max);
}

//Write a C++ program to find maximum between two numbers using conditional operator.
#include<iostream>
using namespace std;
int main()
{
    int a,b,max;
    cout << "Enter two numbers: ";
    cin >> a >> b;
    if (a==b)
    {
        cout << "Equal";
        return 0;
    }
    max = (a > b) ? a : b;
    cout << "Maximum number is " << max;
}

