//Write a C program to check whether a number is positive or negative using conditional operator.
#include<stdio.h>
int main()
{
    int a;
    printf("Enter a number");
    scanf("%d", &a);
    (a>0)?(printf("%d is positive\n",a)):((a<0)?(printf("%d is negative\n",a)):(printf("%d is zero\n",a)));
    return 0;
}

//Write a C++ program to print Hello World.
#include<iostream>
using namespace std;
int main()
{
    int a;
    cout << "Enter a number";
    cin >> a;
    (a>0)?(cout << a << " is positive\n"):((a<0)?(cout << a << " is negative\n"):(cout << " is zero\n"));
    return 0;
}

//Write a Java program to print Hello World
import java.util.Scanner;.
public class Funycode {
    public static void main(String[] args) {
      Scanner z = new Scanner(System.in);
      int a;
      System.out.println("Enter a number");
      a = z.nextInt();
      (a>0)?(System.out.println(a + " is positive\n")):((a<0)?(System.out.println(a + " is negative\n")):(System.out.println(a+"%d is zero\n")));
    }
}

