        else if(y==0)
            System.out.println("This point is in X axis\n");
        else if(x>0 && y>0)
            System.out.println("This point is in first Quardent\n");
        else if(x<0 && y>0)
            System.out.println("This point is in Second Quardent\n");
        else if(x<0 && y<0)
            System.out.println("This point is in Third Quardent\n");
        else
            System.out.println("This point is in Fourth Quardent\n");

    }

    public static void main(String[] args) {
        Scanner z = new Scanner(System.in);
        int x,y;
        System.out.println("Enter X & Y: ");
        x = z.nextInt();
        y = z.nextInt();
        checkQuardent(x,y);
    }
}

