//Write a C program to Find the Volume and Surface Area of Sphere
#include<stdio.h>
#include<math.h>

float surfaceAreaSphere(float r)
{
    float pi=3.14159;
    return 4*pi*r*r;
}
float volumeSphere(float r)
{
    float pi=3.14159;
    return (4/3.0)*pi*r*r*r;
}

int main()
{
    float radius;
    printf("Enter the radius: ");
    scanf("%f",&radius);

    float surface=surfaceAreaSphere(radius);
    float volume=volumeSphere(radius);

    printf("Surface Area of this cuboid is %.2f\n",surface);
    printf("Surface Area of this cuboid is %.2f\n",volume);
    return 0;
}

//Write a C++ program to Find the Volume and Surface Area of Sphere

#include<iostream>
using namespace std;
int square(int num)
{
    return num*num;
}
int main()
{
    int n;
    cout << "Enter a number: ";
    cin >> n;
    int ans=square(n);

    cout << ans;
}

//Write a Java program to show the simple structure of a function.
import java.util.Scanner;
public class Funycode {
    static int square(int num)
    {
        return num*num;
    }

    public static void main(String[] args) {
        Scanner z = new Scanner(System.in);
        int n;
        n = z.nextInt();

        int ans=square(n);

        System.out.println(ans);
    }
}

