//Write a C program to Calculate the Area of a Triangle
#include<stdio.h>
#include<math.h>

float triangleArea(float a,float b,float c)
{
    float s,area;
    s=(a+b+c)/2;

    area=sqrt(s*(s-a)*(s-b)*(s-c));
    return area;
}

int main()
{
    float a,b,c,s;
    printf("Enter 3 sides: ");
    scanf("%f%f%f",&a,&b,&c);

    float area=triangleArea(a,b,c);

    printf("Area is %.2f\n",area);
    return 0;
}

//Write a C++ program to Calculate the Area of a Triangle

#include<iostream>
#include<math.h>
using namespace std;

float triangleArea(float a,float b,float c)
{
    float s,area;
    s=(a+b+c)/2;

    area=sqrt(s*(s-a)*(s-b)*(s-c));
    return area;
}

int main()
{
    float a,b,c,s;
    cout << "Enter 3 sides: ";
    cin >> a >> b >> c;

    float area=triangleArea(a,b,c);

    cout << "Area is " << area;
    return 0;
}

//Write a Java program to show the simple structure of a function.
import java.util.Scanner;
public class Funycode {
    static float triangleArea(int a,int b,int c)
    {
        float s,area;
        s=(a+b+c)/2;

        area=Math.sqrt(s*(s-a)*(s-b)*(s-c));
        return area;
    }

    public static void main(String[] args) {
        Scanner z = new Scanner(System.in);
        float a,b,c,s;
        System.out.println("Enter 3 sides: ");
        a = z.nextInt();
        b = z.nextInt();
        c = z.nextInt();

        float area=triangleArea(a,b,c);
        System.out.println("Area is "+area);
    }
}

