//Write a C program to find maximum between three numbers using conditional operator.
#include <stdio.h>
int main()
{
    int a,b,c,max;
    printf("Enter three numbers: ");
    scanf("%d%d%d", &a,&b,&c);
    if(a=b=c)
    {
        printf("equal");
        return 0;
    }
    max = (a>b && a>c) ?a:(b>c) ?b:c;
    printf("\nMaximum number is %d", a,b,c, max);
}

