//Write a C program to find all the roots of a quadratic equation using switch.
#include<stdio.h>
int main()
{
    int h,m;
    printf("input Hours : ");
    scanf("%d",&h);
    printf("Input Minutes : ");
    scanf("%d",&m);
    switch(h>=12 && h<=24)
    {
    case 1:
        printf("The time is in PM ");
        break;
    case 0:
        switch (h>=0 && h<=11)
        {
        case 1:
            printf("Time is in AM ");
            break;
        case 0:
            printf("Wrong");
            break;
        }
        break;
    }
}

//Write a C++ program to take the hours and minutes as input by the user and the show that whether it is AM or PM by using the switch.
