using namespace std;
int main()
{
    int h,m;
    cout << "input Hours : ";
    cin >> h;
    cout << "Input Minutes : ";
    cin >> m;
    switch(h>=12 && h<=24)
    {
    case 1:
        cout << "The time is in PM ";
        break;
    case 0:
        switch (h>=0 && h<=11)
        {
        case 1:
            cout << "Time is in AM ";
            break;
        case 0:
            cout << "Wrong";
            break;
        }
        break;
    }
}

