//Write a C program in C to get the largest element of an array using the function.
#include<stdio.h>

int findlarge(int ara[],int n)
{
    int mx=-1;
    for(int i=0;i<n;i++)
    {
        if(mx<ara[i])
            mx=ara[i];
    }
    return mx;
}
int main()
{
    int n;
    printf("Enter array size: ");
    scanf("%d",&n);
    int ara[n];
    for(int i=0;i<n;i++)
    {
        scanf("%d",&ara[i]);
    }

    int large=findlarge(ara,n);

    printf("%d is largest element\n",large);
    return 0;
}

//Write a C++ program in C to find the square of any number using the function.

#include<iostream>
using namespace std;

int findlarge(int ara[],int n)
{
    int mx=-1;
    for(int i=0;i<n;i++)
    {
        if(mx<ara[i])
            mx=ara[i];
    }
    return mx;
}
int main()
{
    int n;
    cout << "Enter array size: ";
    cin >> n;
    int ara[n];
    for(int i=0;i<n;i++)
    {
        cin >> &ara[i];
    }
    int large=findlarge(ara,n);
    cout << large << " is largest element\n";
    return 0;
}

