        }
        return mx;
    }

    public static void main(String[] args) {
        Scanner z = new Scanner(System.in);
        Scanner z = new Scanner(System.in);
        int n;
        System.out.println("Enter array size: ");
        n = z.nextInt();
        int[] ara = new int[n];
