//Write a C program to input a string and print it.
#include <stdio.h>
#include<string.h>
int main()
{
    char word[50];
    scanf("%s", word);
    printf("The String is %s.", word);
    return 0;
}

//Write a C++ program to input a string and print it.

#include <iostream>
using namespace std;
int  main()
{
    string word;
    cin >> word;
    cout << "The String is " << word;
    return 0;
}

//Write a Java program to input a string and print it.
import java.util.Scanner;
public class Funycode {
    public static void main(String[] args) {
          Scanner z=new Scanner(System.in);
          String word;
          word = z.nextLine();
          System.out.println("The String is " + word);
      }
}

