#include <string.h>
using namespace std;
int main()
{
    int a[10000];
    int i,n;
    int *p;
    cout<<("Enter size of array: ");
    cin>>n;

    cout<<"\nEnter "<<n<<"  elements"<<endl;

    for (i = 0; i < n; i++)
        cin>>a[i];
    p = &a[n - 1];
    cout<<("\n The elements of array in reverse order are =\n");
    for (i = n; i > 0; i--)
    {
        cout<<(*p)<<endl;
        p--;
    }
}

