//Write a C program to read a file and remove the spaces between two words of its content.
#include <string.h>

int main()
{
    char str[100];
    int  i,j=0;
    printf("Enter  the string : ");
    gets(str);
    for(i=0;str[i];i++)
    {
         str[i]=str[i+j];


         if(str[i]==' '|| str[i]=='\t')
         {
          j++;
          i--;
        }

    }
    printf("string removing spaces:");

    printf("%s",str);
}

//Write a C++ program to read a file and remove the spaces between two words of its content.

#include <iostream>
#include <string.h>
using namespace std;
int main()
{
    char str[100];
    int  i,j=0;
    cout << "Enter  the string : ";
    cin >> str;
    for(i=0;str[i];i++)
    {
         str[i]=str[i+j];
         if(str[i]==' '|| str[i]=='\t')
         {
            j++;
            i--;
         }
    }
    cout << "string removing spaces: " << str;
    return 0;
}

