//Write a C program to Find the Volume and Surface Area of cylinder
#include<stdio.h>

float surfaceAreaCylinder(float radius,float height)
{
    float pi=3.14159;
    return 2*pi*radius*height+2*pi*radius*radius;
}

int main()
{
    float radius,height;
    printf("Enter the radius & height: ");
    scanf("%f%f",&radius,&height);

    float surface=surfaceAreaCylinder(radius,height);

    printf("Surface Area of this cube is %.2f\n",surface);
    return 0;
}

//Write a C++ program to Find the Volume and Surface Area of cylinder

#include<iostream>
using namespace std;

float surfaceAreaCylinder(float radius,float height)
{
    float pi=3.14159;
    return 2*pi*radius*height+2*pi*radius*radius;
}

int main()
{
    float radius,height;
    cout << "Enter the radius & height: ";
    cin >> radius >> height;
    float surface=surfaceAreaCylinder(radius,height);

    cout << "Surface Area of this cube is " << surface;
    return 0;
}

//Write a Java program to Find the Volume and Surface Area of cylinder
import java.util.Scanner;
public class Funycode {
    static float surfaceAreaCylinder(float radius,float height)
    {
        float pi=3.14159;
        return 2*pi*radius*height+2*pi*radius*radius;
    }

    public static void main(String[] args) {
        Scanner z = new Scanner(System.in);
        float radius,height;
        System.out.println("Enter the radius & height: ");
        radius = z.nextFloat();
        height = z.nextFloat();
        float surface=surfaceAreaCylinder(radius,height);
        System.out.println("Surface Area of this cube is "+surface);
    }
}

