//Write a C++ demonstrate example structure pointer (structure with pointer).
#include<stdio.h>
struct student{
    char name[100];
    int id;
};
int main(){
    struct student *sakib;
    struct student p;
    sakib=&p;
    strcpy(sakib->name, "Ahosanul Hasan");
    sakib->id=16105001;
    printf("%s\n",sakib->name);
    printf("%d",sakib->id);
}

//Write a C++ program to demonstrate example structure pointer (structure with pointer).

#include<iostream>
#include <string.h>
using namespace std;
struct student{
    char name[100];
    int id;
};
int main(){
    struct student *sakib;
    struct student p;
    sakib=&p;
    strcpy(sakib->name, "Ahosanul Hasan");
    sakib->id=16105001;
    cout << "\n" << sakib->name << endl;
    cout << "" << sakib->id;
}

