//Write a C program to Find the Perimeter of a Circle, Rectangle and Triangle
#include<stdio.h>

void perimeterOfCircle()
{
    float pi=3.14159,r;
    printf("Enter radius: ");
    scanf("%f",&r);
    float perimeter=2*pi*r;
    printf("Permeter of circle is %.2f: \n",perimeter);
}

void perimeterOfRectangle()
{
    int a,b;
    printf("Enter base & height: ");
    scanf("%d%d",&a,&b);
    float perimeter=2*(a+b);
    printf("Permeter of Rectangle is %.2f: \n",perimeter);
}

void perimeterOfTriangle()
{
    float a,b,c;
    printf("Enter 3 sides of triangle: ");
    scanf("%f%f%f",&a,&b,&c);
    float perimeter=a+b+c;
    printf("Permeter of circle is %.2f: \n",perimeter);
}

int main()
{
    perimeterOfCircle();
    perimeterOfRectangle();
    perimeterOfTriangle();
}

//Write a C++ program to Find the Perimeter of a Circle, Rectangle and Triangle

#include<iostream>
using namespace std;

void perimeterOfCircle()
{
    float pi=3.14159,r;
    cout << "Enter radius: ";
    cin >> r;
    float perimeter=2*pi*r;
    cout << "Permeter of circle is " << perimeter << endl;
}

void perimeterOfRectangle()
{
    int a,b;
    cout << "Enter base & height: ";
    cin >> a >> b;
    float perimeter=2*(a+b);
    cout << "Permeter of Rectangle is "perimeter << endl;
}

void perimeterOfTriangle()
{
    float a,b,c;
    cout << "Enter 3 sides of triangle: ";
    cin >> a >> b >> c;
    float perimeter=a+b+c;
    cout << "Permeter of circle is " << perimeter << endl;
}

int main()
{
    perimeterOfCircle();
    perimeterOfRectangle();
    perimeterOfTriangle();
}

