import java.util.Scanner;
public class Funycode {
    static int convDecimaltoBinary(int n)
    {
        int bin=0;
        int i=1;
        while(n!=0)
        {
            int rem=n%2;
            bin += rem*i;
            i*=10;
            n/=2;
        }
        return bin;
    }

    public static void main(String[] args) {
        Scanner z = new Scanner(System.in);
        int n;
